mapper.update:
UPDATE article

     updateDate = NOW()
    ,title = #{title}
    ,`body` = #{body}

WHERE id = #{id}

mapper.select:
SELECT a.*, m.nickname as author, b.name as board, IFNULL(SUM(r.reactionStatus),0) AS extra__sumReactionPoint, IFNULL(count(case when r.reactionStatus=1 then 1 end),0) AS extra__goodReactionPoint, IFNULL(count(case when r.reactionStatus=-1 then 1 end),0) AS extra__badReactionPoint FROM article a inner join member m on a.memberId = m.id inner join board b on a.boardId = b.id left join reaction as r on a.id =r.relId and r.relDataTypeCode='article' WHERE 1=1
AND a.boardId = #{boardId}
AND m.nickname LIKE CONCAT('%', #{keyword}, '%')
AND a.title LIKE CONCAT('%', #{keyword}, '%')
AND a.`body` LIKE CONCAT('%', #{keyword}, '%')
group by a.id ORDER BY a.id DESC limit #{startsFrom}, #{articlesPerPage}

mapper.update:
UPDATE article
set hitCount=hitCount+1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}
