mapper.select:
SELECT IFNULL(SUM(RP.reactionPoint),0)
FROM reaction AS
         RP
WHERE RP.relDataTypeCode = #{relDataTypeCode}
  AND RP.relId = #{relId}
  AND
    RP.memberId = #{memberId};

mapper.delete:
DELETE
FROM reaction
WHERE memberId = #{memberId}
  AND relDataTypeCode = #{relDataTypeCode}
  AND relId = #{relId}
